import React, { useState } from 'react';

const SearchResults = ({ results }) => {
  const [selectedHtml, setSelectedHtml] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleViewHtml = (content) => {
    setSelectedHtml(content);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedHtml(null);
  };

  // Function to escape HTML for safe display
  const escapeHtml = (unsafe) => {
    if (!unsafe) return '';
    return unsafe
      .replace(/&/g, "&amp;")
      .replace(/</g, "&lt;")
      .replace(/>/g, "&gt;")
      .replace(/"/g, "&quot;")
      .replace(/'/g, "&#039;");
  };

  return (
    <div className="search-results">
      <h2>Search Results</h2>
      <div className="results-list">
        {results.map((result, index) => (
          <div key={index} className="result-card">
            <div className="result-header">
              <div className="result-content">
                <p>{result.content}</p>
              </div>
              <span className="match-score">{result.match_score}% match</span>
            </div>
            <div className="result-footer">
              <span className="path">Path: {result.path}</span>
              <button 
                className="view-html-btn"
                onClick={() => handleViewHtml(result.html_content || result.content)}
              >
                View HTML
              </button>
            </div>
          </div>
        ))}
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <div className="modal-header">
              <h3>HTML Source Code</h3>
              <button className="close-btn" onClick={closeModal}>
                Close
              </button>
            </div>
            <pre className="html-content">
              <code>{selectedHtml ? escapeHtml(selectedHtml) : 'No HTML content available'}</code>
            </pre>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchResults;
